create table if not exists public.leads(id bigint generated always as identity primary key,created_at timestamptz not null default now(),source text,owner text,club text not null,person text,phone text,email text,discipline text,participants int,facilities text,date_window text,pref_location text,camps text[],status text default 'nowy',priority text,notes text,consent text,lead_score int);create table if not exists public.followups(id bigint generated always as identity primary key,lead_id bigint references public.leads(id) on delete cascade,due_at timestamptz not null,note text,status text default 'open');create table if not exists public.activities(id bigint generated always as identity primary key,lead_id bigint references public.leads(id) on delete cascade,ts timestamptz not null default now(),text text not null);alter table public.leads enable row level security;alter table public.followups enable row level security;alter table public.activities enable row level security;create policy "public select leads" on public.leads for select using (true);create policy "public insert leads" on public.leads for insert with check (true);create policy "public update leads" on public.leads for update using (true) with check (true);create policy "public select followups" on public.followups for select using (true);create policy "public insert followups" on public.followups for insert with check (true);create policy "public update followups" on public.followups for update using (true) with check (true);create policy "public select activities" on public.activities for select using (true);create policy "public insert activities" on public.activities for insert with check (true);